//preliminary player sprite
uint8_t block[3][3] = {
        {0x07, 0x02, 0x07},
        {0x02, 0x03, 0x02},
        {0x07, 0x02, 0x07}
};

uint8_t dead = 0;
uint8_t square_row = 11;
uint8_t is_Jumping = 0;
uint8_t jump_count = 0; //tracks how long it has been  jumping

//outputs to the display
void color_in(uint8_t RGB){
    GPIOB->BSRR |= (RGB & 0x01) == 0x01? 1<<11: 1<<(11+16);
    GPIOB->BSRR |= (RGB & 0x02) == 0x02? 1<<13: 1<<(13+16);
    GPIOB->BSRR |= (RGB & 0x04) == 0x04? 1<<9: 1<<(9+16);

    GPIOB->BSRR |= (RGB & 0x10) == 0x10? 1<<10: 1<<(10+16);
    GPIOB->BSRR |= (RGB & 0x20) == 0x20? 1<<12: 1<<(12+16);
    GPIOB->BSRR |= (RGB & 0x40) == 0x40? 1<<8: 1<<(8+16);

}

//delay functions
void nano_wait(unsigned int n) {
    asm(    "        mov r0,%0\n"
            "repeat: sub r0,#83\n"
            "        bgt repeat\n" : : "r"(n) : "r0", "cc");
}
void small_delay(){
    nano_wait(1000);
}

//enables interrupt for PA2 (button pressing)
void enable_exti(void){
    RCC -> APB2ENR |= RCC_APB2ENR_SYSCFGCOMPEN;
    SYSCFG ->EXTICR[0] &= ~SYSCFG_EXTICR1_EXTI2;
    SYSCFG ->EXTICR[0] |=  SYSCFG_EXTICR1_EXTI2_PA;
    EXTI -> RTSR = EXTI_RTSR_TR2;
    EXTI -> IMR = EXTI_IMR_MR2;
    NVIC -> ISER[0] |= 1 << EXTI2_3_IRQn;
    NVIC_SetPriority(EXTI2_3_IRQn, 0);
}

//functions for clocking matrix
void gpio_clock(){
    GPIOA->BSRR |= 1<<8;
    small_delay();
    GPIOA->BRR |= 1<<8;
}
void OE_h(){
    GPIOA->BSRR |= 1<<9;
}
void OE_l(){
    GPIOA->BRR |= 1<<9;
}
void latch_pulse(){
    GPIOB->BSRR |= 1<<4;
    GPIOB->BRR |= 1<<4;
}

void update_address(uint8_t addr){
    GPIOC->BRR |= 0xf;
    GPIOC->BSRR |= addr;
}
void check_music(MIDI_Player *mp){
    if (mp->nexttick == MAXTICKS)
            mp = midi_init(midifile);
}

//draws square x,y coordinates are switched (my bad -Warren) coords are for top left corner of square
void draw_square(int x, int y) {
    if(is_Jumping && jump_count < 8){
       if(jump_count < 4){
           bitmap[x-1][y] = block[x][y];
           bitmap[x][y] = 0x00;
           jump_count++;
       }else{
           bitmap[x + 1][y] = block[x][y];
           bitmap[x][y] = 0x00;
           jump_count++;
       }
       if(jump_count == 8){
           jump_count = 0;
           is_Jumping = 0;
       }
    } else {
        //checks if any of the 3 pixels directly in front of block are not null (collision)
        for (int i = 0; i < 3; i++) {
            if(!(bitmap[x + i][y + 3] == 0x00)) {
                dead = 1;
            }
        }
        //draws player at coordinates
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                bitmap[x + i][y + j] = block[i][j];
            }
        }
    }
}

void EXTI2_3_IRQHandler(void){
    EXTI -> PR = EXTI_PR_PR2;
   // is_Jumping = 1;
  //  draw_square(5, 5);
    square_row = (square_row - 1); //increases position of square, boundaries not set correctly
}

//reverses bit order for 1d array
void translate_frame(uint8_t* frame) {
    for (int i = 0; i < 16; i++) {
        for (int j = 0; j < 64; j++) {
            uint8_t left = frame[i * 64 + j] & 0xf0;
            frame[i * 64 + j] = frame[i * 64 + j] << 4;
            frame[i * 64 + j] = frame[i * 64 + j] | (left >> 4);
        }
    }
}

